import React from "react";
import { useEffect, useState } from "react";

import { styled } from '@mui/material/styles';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import Collapse from '@mui/material/Collapse';
import Avatar from '@mui/material/Avatar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import { red } from '@mui/material/colors';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import { Tooltip,Button, TextField  } from "@mui/material";
import { palette } from "@mui/system";
import EditIcon from '@mui/icons-material/Edit';
import CloseIcon from '@mui/icons-material/Close';

const ExpandMore = styled((props) => {
  const { expand, ...other } = props;
  return <IconButton {...other} />;
})(({ theme, expand }) => ({
  transform: !expand ? 'rotate(0deg)' : 'rotate(180deg)',
  marginLeft: 'auto',
  transition: theme.transitions.create('transform', {
    duration: theme.transitions.duration.shortest,
  }),
}));


function MyProfile({userData}) {
    const [edit, setEdit] = useState(false)


    // const [expanded, setExpanded] = React.useState(false);

//   const handleExpandClick = () => {
//     setExpanded(!expanded);
//   };

  return (    <Card sx={{ m: 2 }}>
      <CardHeader
        avatar={<Avatar alt={userData.name} src={userData.profile_picture_url} sx={{ bgcolor: 'primary.main', height:75, width: 75}}></Avatar>}
        title={<Typography variant="h4" sx={{color: 'primary.main'}} >{userData.name}</Typography>}
        action={edit? <Tooltip title="Cancel">
        <IconButton onClick={()=>setEdit(false)}>
          <CloseIcon sx={{ color: "#c22c2d" }} />
        </IconButton>
      </Tooltip> : <Tooltip title="Edit">
        <IconButton onClick={()=>setEdit(true)}>
          <EditIcon sx={{ color: "#c22c2d" }} />
        </IconButton>
      </Tooltip>}
      />
    <CardContent>
        { edit? <> <TextField fullWidth label="About Me" id="aboutMe" type="text" sx={{m:2}}/>
            <TextField fullWidth label="Wish #1" id="wishOne" type="url"/>
            <TextField fullWidth label="Wish #2" id="wishTwo"type="url"/>
            <TextField fullWidth label="Wish #3" id="wishThree"type="url"/> 

        </> : <>      
        {userData.about_me? 
            <Typography variant="body2" color="text.secondary">{userData.about_me}</Typography> :       
            <Typography variant="body2" color="text.secondary">Tell us a little about yourself</Typography> 
        } {userData.wish_one || userData.wish_two || userData.wish_three ? <>
            <Typography variant="body2" color="text.secondary" sx={{m:1}}>{userData.wish_one} </Typography> 
            <Typography variant="body2" color="text.secondary" sx={{m:1}}>{userData.wish_two} </Typography>
            <Typography variant="body2" color="text.secondary" sx={{m:1}}>{userData.wish_three} </Typography></>:       
            <Typography variant="body2" color="text.primary" sx={{m:1}}>Here you can add up to 3 links from gifts you would like to receive.</Typography> 
        } </>}

    </CardContent>
    <CardActions>
      <Button size="small">Share</Button>
      <Button size="small">Learn More</Button>
    </CardActions>
  </Card>
  );

}

export default MyProfile;
