import React from "react";
import { useEffect } from "react";
import "./style.css";

import {CssBaseline, Tooltip, IconButton, Box, Grid, Typography, Toolbar, AppBar} from '@mui/material';
import LogoutIcon from '@mui/icons-material/Logout';

function App() {

  const handleLogOut = async () => {
    console.log("log out clicked");
    const fetchReq = await fetch("/api/logout", {
      method: "DELETE",
  });
    const response = await fetchReq.json();
    if (response.success === true) {
      // eslint-disable-next-line no-restricted-globals
      location.reload();
  }
    return
  }

  return (
    <> <CssBaseline />
      <Typography variant="h2" align='left' mt={4} sx={{ color: 'primary.main', letterSpacing: 4, m: 2}}>BR Secret Santa</Typography>

          {/* <Tooltip title="Log Out">
            <IconButton onClick={()=>handleLogOut()}>
              <LogoutIcon sx={{ color: "#c22c2d" }} />
            </IconButton>
          </Tooltip>
          <Typography variant="h2" sx={{ color: 'primary.main', letterSpacing: 4, m: 4}}>BR Secret Santa</Typography> */}


    <Box sx={{ flexGrow: 1, mx:'auto', textAlign: 'center', alignContent: "center" }} >
      <Grid container spacing={1}>
        <Grid item xs={10} md={6} >
          <Typography variant="h2" sx={{ color: 'primary.main', letterSpacing: 4, m: 4}}>BR Secret Santa</Typography>
        </Grid>
        <Grid item xs={10} md={6}>
          <Typography variant="h2" sx={{ color: 'primary.main', letterSpacing: 4, m: 4}}>Secret Santa</Typography>
        </Grid>
        <Grid item xs={10} md={6}>
          <Box sx={{ bgcolor: 'primary.main', height: '40vh', borderRadius: 2, boxShadow: 3 ,  m: 1, p:2  }}>event description</Box>
        </Grid>
        <Grid item xs={10} md={6}>
          <Box sx={{ bgcolor: 'secondary.main', height: '40vh', borderRadius: 2, boxShadow: 3,  m: 1, p:2   }}>secret friend + wish list</Box>
        </Grid>
      </Grid>
    </Box>


    <AppBar position="fixed" color="secondary" sx={{ top: 'auto', bottom: 0 }}>
      <Toolbar>
        <Tooltip title="Log Out">
            <IconButton onClick={()=>handleLogOut()}>
              <LogoutIcon  sx={{ color: "primary.main" }} />
            </IconButton>
          </Tooltip>
      </Toolbar>
      </AppBar>
    </>
  );
}

export default App;
