import React from "react";
import { useEffect, useState } from "react";

import { styled } from '@mui/material/styles';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import Collapse from '@mui/material/Collapse';
import Avatar from '@mui/material/Avatar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import { red } from '@mui/material/colors';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import { Tooltip,Button, TextField  } from "@mui/material";
import { palette } from "@mui/system";

const ExpandMore = styled((props) => {
  const { expand, ...other } = props;
  return <IconButton {...other} />;
})(({ theme, expand }) => ({
  transform: !expand ? 'rotate(0deg)' : 'rotate(180deg)',
  marginLeft: 'auto',
  transition: theme.transitions.create('transform', {
    duration: theme.transitions.duration.shortest,
  }),
}));


function MyProfile({userData}) {
    const [aboutMeEdit, setAboutMeEdit] = useState(false)


    // const [expanded, setExpanded] = React.useState(false);

//   const handleExpandClick = () => {
//     setExpanded(!expanded);
//   };

  return (    <Card sx={{ maxWidth: 345 }}>
      <CardHeader
        avatar={<Avatar alt={userData.name} src={userData.profile_picture_url} sx={{ bgcolor: 'primary.main', fontWeight:700, fontSize: 'large' }}></Avatar>}
        title={<Typography variant="h4" sx={{color: 'primary.main'}} >{userData.name}</Typography>}
      />
    <CardContent>
        {aboutMeEdit ? <>
            <TextField id="aboutMe" label="About Me" variant="outlined" defaultValue={userData.about_me}/>
            <button size="small" variant="text" >Save</button> 
            </> :<>{userData.about_me ? 
        <Button variant="text" onClick={()=> setAboutMeEdit(true)}>{userData.about_me}</Button> :       
        <Button variant="text" onClick={()=> setAboutMeEdit(true)}>Tell us a little about yourself</Button> 
        }</>} 

    </CardContent>
    <CardActions>
      <Button size="small">Share</Button>
      <Button size="small">Learn More</Button>
    </CardActions>
  </Card>
  );

}

export default MyProfile;
