import React from "react";
import { useEffect, useState } from "react";
import { Card, CardMedia, CardContent, Typography } from "@mui/material";

export default function SecretGroup({userData}) {
    const [groupData, setGroupData] = useState();

    const getSecretData = async () => {
        const fetchReq = await fetch("/api/get-secret-group")
        const response = await fetchReq.json();
        setGroupData(response)
        console.log("hello ");
        const draw = new Date(response.draw_date).toLocaleString(
            "en-US",
              {
                month: "short",
                day: "2-digit",
                year: "numeric",
              }
          );
        console.log("draw", draw);
    }

    useEffect(()=> {
        getSecretData()
    },[])

  return ( 
  <Card sx={{ height: {md: "70vh", m: 1}}}>
    {groupData && <> 
        <CardMedia
        component="img"
        height="55%"
        image={groupData.group_photo_url}
        alt="2 girls celebrating the holidays"
        /> 
        <CardContent>
            <Typography variant='h4'>{groupData.group_name}</Typography>
            <Typography variant="body2"><strong>Draw Date: </strong> {groupData.draw_date}</Typography>
            <Typography variant="body2"><strong>Event Date: </strong> {groupData.event_date}</Typography>


      </CardContent></>




      }


  </Card>
  );

}
